,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH tl AS (',
'    SELECT',
'        fecha_liquidacion, no_liquidacion, ROWID, monto_gastos, fecha_ingreso FROM zam_trafico_liquidacion',
'    WHERE',
'       -- no_liquidacion IN ( :P21_NUM_LIQUIDACION) AND',
'      trunc(FECHA_INGRESO) = to_date(:P21_FECHA_LIQUIDACION,''DD/MM/RR'') and  --busqueda por fecha de liquidacion ',
'        --REPLICADO_AR = ''N'' AND ',
'        status_liq = ''A''',
'), lq AS (',
'    SELECT vl.no_liquidacion,vl.no_viaje,id_personal_nombre FROM zam_trafico_viaje_liq vl, tl WHERE vl.no_liquidacion = tl.no_liquidacion',
'    UNION',
'select T1."liquidacion_operador2" as no_liquidacion,T1."no_viaje",T2."nombre" as id_personal_nombre',
'from TRAFICO_VIAJE@HSMSSQLDLV.ESTRELLAROJA.COM.MX T1, PERSONAL_PERSONAL@HSMSSQLDLV.ESTRELLAROJA.COM.MX T2,tl',
'WHERE t1."id_operador2" = t2."id_personal"(+)  and t1."liquidacion_operador2" =tl.no_liquidacion',
'), gu AS (',
'    SELECT "no_viaje", "num_guia",  "no_guia" FROM trafico_guia@hsmssqldlv.estrellaroja.com.mx g,lq WHERE g."no_viaje" = lq.no_viaje and G."status_guia"!=''B''',
')',
'SELECT DISTINCT',
'    dn.unidad_negocio,',
'    ''LIQUIDACION''                                                                           tipo_doc,',
'    ''PROPIO''                                                                                tipo_movimiento,',
'    dn.rfc                                                                                  rfc_dliver,',
'    ''CONTINGENTE''                                                                           nombre,',
'    ''CONTINGENTE''                                                                           rfc,',
'    substr(tg.num_guia, 1, instr(tg.num_guia, ''-'') - 1)                                     desc_plaza,',
'    substr(tg.num_guia, 1, instr(tg.num_guia, ''-'') - 1) || ''LIQ-'' || tl.no_liquidacion      numero_documento,',
'    tl.fecha_liquidacion                                                                    fecha_ingreso,',
'    substr(tg.num_guia, 1, instr(tg.num_guia, ''-'') - 1) || ''LIQ-'' || tl.no_liquidacion      ref_aplicacion,',
'    tl.no_liquidacion                                                                       id_movimiento,',
'    ''''                                                                                      no_guia,',
'   -- tl.rowid,',
'    ''SIN ASIGNAR''                                                                           destino_servicio,',
'    ''CRE''                                                                                   forma_pago,',
'    ''NUM LIQUIDACION:'' || tl.no_liquidacion                                                 linea_pago,',
'    ''N/A''                                                                                   unidad_medida,',
'    tl.monto_gastos                                                                         subtotal,',
'    ''AP_IVA16''                                                                              ar_iva_exento,',
'    tl.monto_gastos                                                                         total,',
'    tl.fecha_ingreso                                                                        creation_date,',
'    tg.id_personal_nombre                                                                   personal_nombre',
'FROM',
'    xxer_datos_negocio_tbl  dn, tl,',
'    (',
'        SELECT',
'            l.no_liquidacion,',
'            nvl(max(g."num_guia"), max(g2."num_guia")) num_guia, l.id_personal_nombre',
'        FROM lq  l',
'            LEFT JOIN gu  g ON l.no_viaje = g."no_viaje" AND substr(g."num_guia", 1, instr(g."num_guia", ''-'') - 1) <> ''VCT''',
'            LEFT JOIN gu  g2 ON l.no_viaje = g2."no_viaje" AND substr(g2."num_guia", 1, instr(g2."num_guia", ''-'') - 1) = ''VCT''',
'        GROUP BY l.no_liquidacion, l.id_personal_nombre',
'    ) tg',
'WHERE',
'        1 = 1',
'    AND dn.unidad_negocio = ''DLIVER''',
'    AND tl.no_liquidacion = tg.no_liquidacion',
'    AND tg.num_guia IS NOT NULL',
'    AND not exists (select 1 ',
'                from iner_xxer_puente_header_tbl tp  ',
'                where tp.numero_documento = substr(tg.num_guia, 1, instr(tg.num_guia, ''-'') - 1) || ''LIQ-'' || tl.no_liquidacion ',
'                and tp.tipo_documento=''LIQUIDACION'');'))
