  CREATE TABLE "XXER_TBL_REPORT_REPSE" 
   (
   
   "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY, 
    "RFC"           VARCHAR2(20 BYTE) NOT NULL ENABLE,
	"PROVEEDOR"      VARCHAR2(100 BYTE),
    "PARAMETER1"         VARCHAR2(10 BYTE),
    "PARAMETER2"         VARCHAR2(10 BYTE),
	"PARAMETER3"         VARCHAR2(10 BYTE),
	"PARAMETER4"         VARCHAR2(10 BYTE),
	"PARAMETER5"         VARCHAR2(10 BYTE),
	"PARAMETER6"         VARCHAR2(10 BYTE),
	"PARAMETER7"         VARCHAR2(10 BYTE),
	"PARAMETER8"         VARCHAR2(10 BYTE),
	"PARAMETER9"         VARCHAR2(10 BYTE),
	"PARAMETER10"         VARCHAR2(10 BYTE),
	"PARAMETER11"         VARCHAR2(10 BYTE),
	"PARAMETER12"         VARCHAR2(10 BYTE),
	"PARAMETER13"         VARCHAR2(10 BYTE),
	"CUMPLIMIENTO"   VARCHAR2(10 BYTE),
	"CREATION_DATE"         DATE,
    "CREATED_BY"            VARCHAR2(150 BYTE),
    "LAST_UPDATE_DATE"      DATE,
    "LAST_UPDATED_BY"       VARCHAR2(150 BYTE), 


	 
   CONSTRAINT "XXER_TBL_REPORT_REPSE_PK" PRIMARY KEY ("ID")
   ) ;
      COMMENT ON COLUMN "XXER_TBL_REPORT_REPSE"."ID" IS 'id generado del registro';


  CREATE OR REPLACE EDITIONABLE TRIGGER "AUDIT_TRIGGER_TBL_REPORT_REPSE" BEFORE INSERT OR UPDATE
  ON XXER_TBL_REPORT_REPSE FOR EACH ROW
  DECLARE
  v_user varchar(50):=null;
BEGIN
if (NVL(APEX_UTIL.GET_CURRENT_USER_ID,0)=0)
  then
     v_user:=SYS_CONTEXT('USERENV', 'HOST', 30);
  else
    v_user:=APEX_UTIL.GET_USERNAME(APEX_UTIL.GET_CURRENT_USER_ID);
  end if; 
  IF INSERTING THEN
  :new.CREATION_DATE:=sysdate;
   :new.CREATED_BY:=v_user;
  ELSIF UPDATING THEN
  :new.LAST_UPDATE_DATE:=sysdate;
   :new.LAST_UPDATED_BY:=v_user; 
  END IF;
END;

DROP TABLE  "XXER_TBL_REPORT_REPSE"  CASCADE CONSTRAINTS;   


